INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2014-04-07 13:33:15', '2014-04-07 13:33:15', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'trash', 'open', 'open', '', 'hello-world', '', '', '2014-04-08 08:39:35', '2014-04-08 08:39:35', '', 0, 'http://oracle.mointernational-clients.co.uk/?p=1', 0, 'post', '', 1),
(2, 1, '2014-04-07 13:33:15', '2014-04-07 13:33:15', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://oracle.mointernational-clients.co.uk/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'trash', 'open', 'open', '', 'sample-page', '', '', '2014-04-08 08:40:40', '2014-04-08 08:40:40', '', 0, 'http://oracle.mointernational-clients.co.uk/?page_id=2', 0, 'page', '', 0),
(4, 1, '2014-04-07 15:17:47', '2014-04-07 15:17:47', '', 'FSI Overview', '', 'publish', 'open', 'open', '', 'fsi-overview', '', '', '2014-04-08 14:07:33', '2014-04-08 14:07:33', '', 0, 'http://oracle.mointernational-clients.co.uk/?p=4', 1, 'nav_menu_item', '', 0),
